import React from 'react'
import logo from '../Component/image/rupplogo.png';
import { Link } from 'react-router-dom';
import { useState, useEffect, useRef } from 'react';

const Login = () => {

    const canvasRef = useRef(null);
    const [captchaAnswer, setCaptchaAnswer] = useState(null);
    const [userInput, setUserInput] = useState("");
    const [timeLeft, setTimeLeft] = useState(10);
    const [attempts, setAttempts] = useState(0);

    useEffect(() => {
        generateCaptcha();

        // Countdown Timer (Resets when new CAPTCHA is generated)
        const timer = setInterval(() => {
            setTimeLeft((prev) => (prev > 0 ? prev - 1 : 0));
        }, 1000);

        return () => clearInterval(timer);
    }, []);

    const generateCaptcha = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");
        canvas.width = 200;
        canvas.height = 50;

        // Generate random math problem
        const num1 = Math.floor(Math.random() * 10) + 1;
        const num2 = Math.floor(Math.random() * 10) + 1;
        const question = `${num1} × ${num2} = ?`;
        setCaptchaAnswer(num1 * num2);

        // Style the CAPTCHA
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.fillStyle = "#333";
        ctx.font = "20px Arial";
        ctx.fillText(question, 30, 30);

        // Add Noise
        for (let i = 0; i < 5; i++) {
            ctx.beginPath();
            ctx.moveTo(Math.random() * canvas.width, Math.random() * canvas.height);
            ctx.lineTo(Math.random() * canvas.width, Math.random() * canvas.height);
            ctx.strokeStyle = "red";
            ctx.stroke();
        }

        // Reset Time and Attempts
        setTimeLeft(10);
        setAttempts(0);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (timeLeft === 0) {
            alert("CAPTCHA expired! Please try again.");
            generateCaptcha();
            return;
        }

        if (parseInt(userInput) === captchaAnswer) {
            alert("CAPTCHA Verified! Logging in...");
            // Proceed with Admin Authentication Logic (API Call or Redirect)
        } else {
            setAttempts(attempts + 1);
            if (attempts >= 2) {
                alert("Too many failed attempts. Refreshing CAPTCHA.");
                generateCaptcha();
            } else {
                alert("Wrong CAPTCHA. Try again.");
            }
        }
    };

  return (
    <div class="flex flex-col w-full  overflow-hidden relative min-h-screen radial-gradient items-center justify-center g-0 px-4">
        <div class="justify-center items-center w-full card lg:flex max-w-md ">
            <div class=" w-full card-body">
                    <a class="py-4 block"><img src={logo} alt="" class="mx-auto h-32 w-32"/></a>
                    <p class="mb-4 text-gray-500 text-lg font-semibold text-center">Department Management Information System</p>
                <form onSubmit={handleSubmit}>
                    <div class="mb-4">
                        <label for="forUsername"
                        class="block text-sm font-semibold mb-2 text-gray-600">Username</label>
                    <input type="text" id="forUsername"
                        class="py-3 px-4 block w-full border-gray-200 rounded-md text-sm focus:border-blue-600 focus:ring-0 " aria-describedby="hs-input-helper-text"/>
                    </div>

                    <div class="mb-6">
                        <label for="forPassword"
                        class="block text-sm font-semibold mb-2 text-gray-600">Password</label>
                        <input type="password" id="forPassword"
                        class="py-3 px-4 block w-full border-gray-200 rounded-md text-sm focus:border-blue-600 focus:ring-0 " aria-describedby="hs-input-helper-text"/>
                    </div>

                    {/* CAPTCHA Section */}
                    <div className="mb-4">
                        <label className="block text-sm font-semibold mb-2 text-gray-600">
                            CAPTCHA (Solve to Login)
                        </label>
                        <div className='flex'>
                            <canvas ref={canvasRef} className="border border-gray-400 rounded-md"></canvas>
                            <button
                                onClick={generateCaptcha}
                                className="my-autow-full text-sm text-gray-600 hover:underline">
                                <svg className='size-7 ml-4' xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2">
                                    <path d="M20 11a8.1 8.1 0 0 0 -15.5 -2m-.5 -4v4h4"></path>
                                    <path d="M4 13a8.1 8.1 0 0 0 15.5 2m.5 4v-4h-4"></path>
                                </svg>
                            </button>
                        </div>
                        <input
                            type="text"
                            placeholder="Enter CAPTCHA Answer"
                            value={userInput}
                            onChange={(e) => setUserInput(e.target.value)}
                            className="py-2 px-3 block w-full border-gray-300 rounded-md text-sm mt-2"
                            required
                        />

                        <p className="text-xs text-gray-500 mt-1">Time left: {timeLeft}s | Attempts: {attempts}/3</p>
                    </div>

                    <button class="grid my-6 w-full">
                        <Link to="/home" class="btn py-[10px] text-base text-white font-medium hover:bg-blue-700">Sign In</Link>
                    </button>
                </form>
            </div>
        </div>
	</div>
  )
}

export default Login
